
public class Array17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] a={6,0,3,4,9,8,10,-1,11,15};
		for(int i=0;i<=9;i++)
		{
			if(a[i]%2==0)
			{
				System.out.println(a[i]);
			}
		}
		for(int i=0;i<=9;i++)
		{
			if(a[i]%2!=0)
			{
				System.out.println(a[i]);
			}
		}


	}

}
